mapper.select:
select * from t_consumer
where 1 = 1

    And id = #{id}


    And consumer_name = #{consumerName}


    And phone = #{phone}


    And open_id = #{openId}


    And credits = #{credits}


    And order_times = #{orderTimes}


    And total_prices = #{totalPrices}


    And sex = #{sex}


    And birthday = #{birthday}

limit #{startIndex},#{endIndex}

